<?php	if ( ! defined('BASEPATH')) exit('No direct script access allowed');		class Quote extends CI_Controller	{				public function index()		{			$this->display_quote();		}				/**			@name: display_quote()			@uses: Display the quote		*/		public function display_quote()		{			// Load the model		    $this->load->model('Menu_model');			$this->load->model('Video_model');			$this->load->model('Ads_model');			$this->load->model('Settings_model');			$this->load->model('Content_model');			$this->load->model('Ads_model');			$this->load->model('Social_media_model');						// Get the menu            $data['header_menu'] = $this->Menu_model->get_menu_header();            $data['footer_menu'] = $this->Menu_model->get_menu_footer();						// Get the 5 latest added videos			$data['video'] = $this->Video_model->get_five_latest_videos();						// Get the newest ad			$data['ads'] = $this->Ads_model->get_newest_ads();						// Get the email address			$data['email_address'] = $this->Settings_model->get_email_address();						// Get the company information			$data['company_info'] = $this->Settings_model->get_company_info();						// Get the company slogan			$data['slogan'] = $this->Content_model->get_content_for_slogan();						// Get the content for home			$data['home'] = $this->Content_model->get_content_for_home();						// Get the latest ads			$data['latest'] = $this->Ads_model->get_latest_ads_3();						// Get the socila media 			$data['social_media'] = $this->Social_media_model->get_social_media();						$this->load->view('header', $data);			$this->load->view('banner');			$this->load->view('home/home');			$this->load->view('services');			$this->load->view('footer');		}			}?>