<div class="container">	<div class="row">		<div class="span12 well_body">			<h3 class="content_header">My Shopping Cart (<?php echo $this->cart->total_items() ?>)</h3>			<table class="table table-striped table-hover content_detail">				<tr>					<th>Products</th>					<th>Price</th>					<th>Quantity</th>					<th>Total</th>				</tr>				<?php					$cart = $this->cart->contents();					if($cart){						foreach($cart as $list){				?>							<tr>								<td>									<div class="row">										<div class="span3">											<img src="<?php echo base_url('uploads/products/'.$list['id'].'/'.$list['options']['img']) ?>" alt="<?php echo $list['name'] ?>" title="<?php echo $list['name'] ?>"/>										</div>										<div class="span3">											<h3 class="content_header"><?php echo $list['name'] ?></h3>											<div class="content_detail"><?php echo $list['options']['description'] ?></div>											<br />											<div class="content_header">Sizes</div>											<table class="table table-striped content_detail">												<tr>													<th>Size</th>													<th>Quantity</th>													<th>Price</th>													<th>Line Total</th>												</tr>												<?php													$product_detail = $list['options']['quantity'];													if($product_detail){														foreach($product_detail as $list1){															$detail = explode('-', $list1);															if($detail[0] != 0){																											?>																<tr>																	<td><?php echo $detail[1] ?></td>																	<td><?php echo $detail[0] ?></td>																	<td><?php echo $currency[0]['data'].' '.number_format($detail[2],2,'.',',') ?></td>																	<td><?php echo $currency[0]['data'].' '.number_format($detail[3],2,'.',',') ?></td>																</tr>												<?php															}														}													}												?>											</table>											<button class="btn btn-warning btn-small" onclick="delete_item('<?php echo $list['rowid'] ?>')">Remove Item</button>										</div>									</div>								</td>								<td><?php echo $currency[0]['data'].' '.number_format($list['price'],2,'.',',') ?></td>								<td><?php echo $list['qty'] ?></td>								<td><?php echo $currency[0]['data'].' '.number_format($list['options']['line_amount'],2,'.',',') ?></td>							</tr>				<?php						}					} else {				?>						<tr>							<td colspan="4">								<div class="alert alert-info">									<strong>Your cart is empty.</strong> 								</div>							</td>						</tr>				<?php					}				?>			</table>						<div id="myModal" class="modal hide fade" tabindex="-1" role="dialog" aria-labelledby="myModalLabel" aria-hidden="true">				<div class="modal-header">					<button type="button" class="close" data-dismiss="modal" aria-hidden="true">Ã—</button>					<h3 id="confirm_title" class="content_header">&nbsp;</h3>				</div>				<div class="modal-body">					<p id="confirm_message" class="content_detail">&nbsp;</p>				</div>				<div class="modal-footer">					<button class="btn btn-danger btn-small" data-dismiss="modal" aria-hidden="true">No</button>					<button class="btn btn-success btn-small" id="cmdDelete1">Yes</button>				</div>			</div>						<input type="hidden" name="rowid" id="rowid" value=""/>			<script type="text/javascript">								function delete_item(rowid)				{					$('#rowid').val(rowid);					$('#confirm_title').html('Delete Item');					$('#confirm_message').html('Proceed in deleting the item from the cart?');					$('#myModal').modal('show');				}								$('#cmdDelete1').click				(					function()					{						$.post						(							'<?php echo site_url("order/delete-item") ?>',							{								rowid: $('#rowid').val()							},							function(data)							{								insert_header_message('Delete Item from Cart');								insert_detail_message('Item successfully removed from the cart.');								display_message();								setTimeout('refresh_page()', 3000);							}						);					}				);								function refresh_page()				{					window.location = '<?php echo current_url() ?>';				}							</script>						<table class="table table-striped content_detail" width="100%">				<tr>					<td width="80%"><div align="right">Sub Total Amount:</div></td>					<td width="20%"><div><?php echo $currency[0]['data'].' '.number_format($this->cart->total(),2,'.',','); ?></div></td>				</tr>				<tr>					<td><div align="right">Shipping Amount:</div></td>					<td><div><?php echo $currency[0]['data'].' 0.00' ?></div></td>				</tr>				<tr>					<td><div align="right">Discount Amount Amount:</div></td>					<td><div><?php echo $currency[0]['data'].' 0.00' ?></div></td>				</tr>				<tr>					<td><div class="font_size_18" align="right">Total Amount:</div></td>					<td><div class="font_size_18"><?php echo $currency[0]['data'].' '.number_format($this->cart->total(),2,'.',','); ?></div></td>				</tr>			</table>			<hr />			<div class="row">				<div class="span6">					<div class="row">						<div class="span4">							<form class="form-horizontal content_detail" method="POST" enctype="multipart/form-data">								<div class="control-group">									<label class="control-label" for="discount_code">Discount Code</label>									<div class="controls"><input type="text" id="discount_code" name="discount_code" placeholder="Discount Code" class="span2"></div>								</div>							</form>						</div>						<div class="span2"><button type="button" name="cmdRecalculate" id="cmdRecalculate" class="btn btn-warning btn-small">Recalculate</button></div>					</div>				</div>				<div class="span6">					<div align="right" class="content_detail">						<a href="<?php echo site_url('shopping/view-items'); ?>" class="btn btn-info btn-large">Continue Shopping</a>						<a href="<?php echo site_url('order/check-out') ?>" class="btn btn-success btn-large">Checkout</a>					</div>				</div>			</div>		</div>	</div></div>